<template>
  <div>
    <div
      class="returnDiv"
      @click="toInfo"
    >
      <image
        class="iconImg"
        src="/static/png/arrow-gray.png"
      />
    </div>
    <div>
      <div
        class="whiteBtn topBtn "
        @click="position"
      >
        <image
          class="iconImg"
          src="/static/png/position.png"
        />
        <span>
          当前所在位置
        </span>
      </div>
      <map
        id="map"
        :longitude="log"
        :latitude="lat"
        scale="14"
        :markers="markers"
        show-location
        :include-points="markers"
        style="width: 100%; height: 100vh;"
        @markertap="markertap"
      />
    </div>
  </div>
</template>
<script>
import shareMix from "@/mixins/mixin";
import { promisify } from "@/utils/index";

export default {
  mixins: [shareMix],
  data() {
    return {
      log: '113.324520',
      lat: '23.099994',
      markers: []
    }
  },
  methods: {
    markertap(e) {
      const that = this;
      const placeId = e.markerId
      this.$request
      .post("/location/getPlace.do"	,{
        placeId
      })
      .then(res => {
       const data = res.list[0];
       const markets = that.markers;
        markets.forEach(val=>{
          if(val.id == placeId){
            val['callout'] = {
              content: `场所名称：${data.fAreaName}\n 法人：${data.fVillagePrincipal} 紧急联系人：${data.fAreaPrincipal} 电话：${data.fAreaPhone} 地址：${data.fAreaAddress}` || '',
              fontSize: 14,
              bgColor: "#FFF",
              borderWidth: 1,
              borderColor: "#CCC",
              padding: 4,
              display: "ALWAYS",
              textAlign: "center"
            }
          }
        })
        that.markers = markets;
      })
      .catch(err => {
        return wx.showToast({
          title: "获取失败",
          icon: "none"
        });
      }); 
    },
    position(){
      const that = this; 
      wx.getLocation({
        type: 'wgs84',
          success (res) {
            const latitude = res.latitude
            that.lat =  res.latitude
            console.log('latitude: ', latitude);
            const longitude = res.longitude
            that.log = res.longitude
            console.log('longitude: ', longitude);
            const speed = res.speed
            console.log('speed: ', speed);
            const accuracy = res.accuracy
            console.log('accuracy: ', accuracy);
            // that.markers[{}]
          }
        })
    },
    sendPosition(){
      console.log(2)
    },
    toInfo(){
      // wx.reLaunch({
      //   url: "/pages/info/index"
      // });

      wx.navigateBack({
        delta: 1,  // 返回上一级页面。
        success: function() {}
        })
    },
    getData(){
      const userId = wx.getStorageSync("userId");
      const value = wx.getStorageSync("userAddress");
      let province = '';
      if (value) {
        province = value.province
        // Do something with return value
      }
      this.$request
      .post("/location/getPosition.do"	,{
        userId,
        province,
        page:1
      })
      .then(res => {
       const data = res.list;
       const markers = [];
       data.forEach(val=>{
        const obj = {};
        obj.id = val.fID;
        obj.latitude = val.fPositionY;
        obj.longitude = val.fPositionX;
        obj.title =  val.fAreaName;
        obj.electricity = val.electricity; 
        obj.smoke = val.smoke;
        obj.video =val.video;
        obj.water =val.water; 
        obj.width = 20;
        obj.height = 20;
        obj.co =val.co;
        obj.iconPath= '../../static/png/position.png'
        markers.push(obj)
       })
        
       this.markers = markers;
      })
      .catch(err => {
        return wx.showToast({
          title: "获取失败",
          icon: "none"
        });
      });      
    },
    goPosition(x, y) {
       wx.openLocation({ // 打开微信内置地图，实现导航功能（在内置地图里面打开地图软件）
          latitude: +y,
          longitude: +x,
          name:'设备位置',
          success:function(res){
            console.log(res);
          },
          fail:function(res){
            console.log(res);
          }
        })
    }
  },
  onLoad(opt) {
    let {positionX, positionY, isalarm} = opt
    if(isalarm) {
      this.goPosition(positionX, positionY)
    } else {
      this.position();
      this.getData();
    }
  },
  onShow(){
    
  }
};
</script>
